//prints out the numbers from 10 to 1 (decrementing order)
public class Homework2Problem4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 10; i >= 1; i--) {
			if (i > 1) {
				System.out.print(i + " ");
			} else {
				System.out.println(i);
			}
		}
	}

}
